function keydownh(e) {
	if(fresh)
	{
		fresh = false;
		$("#inst").fadeOut(10000);
	}
    var p = e.which;
    step(p);
}

function alignInst() {
    $("#inst").css("top", 70+$("#inst").width()/2);
    $("#inst").css("left", 70+$("#inst").height()-$("#inst").width()/2);
    $("#inst").css("-webkit-transform","rotate(-90deg)");
    $("#inst").css("-moz-transform", "rotate(-90deg)");
    $("#inst").css("filter", "progid:DXImageTransform.Microsoft.BasicImage(rotation=3)");
}

function next(th, f, t) {
    th.timeout = window.setTimeout(function () { f(th); }, t);
}

Array.prototype.randIn = function() { return this[Math.floor(Math.random() * this.length)]; };


function step(c) {

    if (c == '8'){
	currentbananas = $("#bananas").text();
	bananalength = currentbananas.length;
	if (bananalength>0)
	{
		deleted = currentbananas.substring(0,bananalength-1);
		$("#bananas").text(deleted);
	}
    }

    else if (chr(c) == "b") {
//	award("*");
	$("#story").append(chr(c));
    }


    else $("#story").append(chr(c));

    if (isHamlet($("#story").text()))
	{
		award("*");
	}
}


function isBanana(t) {
	trimmed = t.substring(checkedlength,t.length);
	if (trimmed.match(/.?banana.?/))
	{
		checkedlength = t.length;
		return true;
	}
	else return false;
}

function isHamlet(t) {
//first, we're only looking at the text since the last awarding of banana
	trimmed = t.substring(checkedlength,t.length);
//and we're stripping out spaces
	trimmed = trimmed.replace(/[^A-Za-z]/g, "")
//now see how long of a match we can find
	matchedlength=0;
	regexstring="";
	if(trimmed.length>necessarymatchlength)
	{
		for(var i=0;i<trimmed.length/chunksize; i++)
		{
			lastchunk = trimmed.substring(trimmed.length-(chunksize*(i+1)), trimmed.length-(chunksize*(i)));
			regexstring = lastchunk.concat(regexstring);
			
			thisregex = new RegExp(regexstring, "i");
			if(hamlet.match(thisregex) && regexstring.length>necessarymatchlength) 
			{
			//	log(regexstring);
				checkedlength = t.length;
				return true;
			}
			else if(regexstring.length>2*necessarymatchlength) 
			{
			//	log(regexstring);
				checkedlength = t.length;
				return false;
			}
		}	
	}

}



function hamlet(){
	hamlet = "";
}
